mapper.select:
select book_id, book_name, nation, type, length, theme, store_date, left_amount, upload_amount, download_amount, author, summary from Book where 1=1
and (book_name like '%${book.bookName}%' or author like '%${book.bookName}%')
and nation=#{book.nation}
and type=#{book.type}
and length=#{book.length}
and theme=#{book.theme}
limit #{index},5

select count(*) 
from Book where 1=1 

		and (book_name like '%${bookName}%' or author like '%${bookName}%')  


		and nation=#{nation}


		and type=#{type}


		and length=#{length}


		and theme=#{theme}

mapper.update:
update book
set download_amount = download_amount + left_amount, left_amount = 0
where book_id = #{bookId}

mapper.select:
select upload_amount 
from book
where book_id = #{bookId}
