mapper.update:
update borrow
set ret_flag = 1, ret_time = #{retTime}
where id=#{id}

update book
set left_amount = left_amount + 1
where book.book_id = #{bookId}

update book
set left_amount = left_amount - 1
where book.book_id = #{bookId}

mapper.select:
select count(*)
from borrow
where borrow_time between #{start} and #{today}

select count(*)
from borrow
where borrow_time between #{start} and #{today}
